mapper.select:
SELECT COUNT(*)
FROM FREE_BOARD
WHERE S_NO = #{s_no}



SELECT B_NO, S_NO, B_TITLE, B_WRITER, B_CONTENT, B_REGDATE, B_CNT
FROM
(
	SELECT ROWNUM N, B_NO, S_NO, B_TITLE, B_WRITER, B_CONTENT, B_REGDATE, B_CNT
	FROM FREE_BOARD
	WHERE S_NO = #{studyno}
	ORDER BY B_NO DESC
)
WHERE N >= #{startRow} AND N <= #{endRow}


			SELECT B_NO, S_NO, B_TITLE, B_WRITER, B_CONTENT, B_REGDATE, B_CNT 
			FROM
			(
    			SELECT ROWNUM R , B_NO, S_NO, B_TITLE, B_WRITER, B_CONTENT, B_REGDATE, B_CNT
    			FROM FREE_BOARD
    			WHERE S_NO = #{s_no}
    			ORDER BY B_NO DESC
			)
			WHERE R < 
                	(
                 	   SELECT R
                	   FROM 
              	       (
              	       SELECT ROWNUM R , B_NO, S_NO, B_TITLE, B_WRITER, B_CONTENT, B_REGDATE, B_CNT
                	   FROM FREE_BOARD
                	   WHERE S_NO =#{s_no}
               	       ORDER BY B_NO DESC
               	       )
               	       WHERE B_NO = #{b_no}
            	    ) +3 
            AND
                 R >
                   (
                     SELECT R
                     FROM 
                     (
                     SELECT ROWNUM R , B_NO, S_NO, B_TITLE, B_WRITER, B_CONTENT, B_REGDATE, B_CNT
                     FROM FREE_BOARD
                     WHERE S_NO =#{s_no}
                     ORDER BY B_NO DESC
                     )
                     WHERE B_NO = #{b_no}
                    ) -3
