mapper.select:
SELECT COUNT(*)
FROM MEET_BOARD
WHERE S_NO = #{s_no}


SELECT MEET_NO, MEET_REGDATE, MEET_ADDR, MEET_TITLE, MEET_WRITER, VOTE_STARTDATE, VOTE_ENDDATE
FROM
(
	SELECT ROWNUM N, MEET_NO, MEET_REGDATE, MEET_ADDR, MEET_TITLE, MEET_WRITER, VOTE_STARTDATE, VOTE_ENDDATE
	FROM MEET_BOARD
	WHERE S_NO = #{studyno}
	ORDER BY MEET_NO DESC
)
WHERE N >= #{startRow} AND N <= #{endRow}
