mapper.select:
SELECT COUNT(*)
FROM DATA_BOARD
WHERE S_NO = #{s_no}

SELECT COUNT(*)
FROM DATA_BOARD
WHERE S_NO = #{s_no}


SELECT B_NO, S_NO, B_TITLE, B_WRITER, B_CONTENT, B_REGDATE, B_CNT
FROM
(
	SELECT ROWNUM N, B_NO, S_NO, B_TITLE, B_WRITER, B_CONTENT, B_REGDATE, B_CNT
	FROM DATA_BOARD
	WHERE S_NO = #{s_no}   
	ORDER BY B_NO DESC
)
WHERE N >= #{startRow} AND N <= #{endRow}


SELECT B_NO, S_NO, B_TITLE, B_WRITER, B_CONTENT, B_REGDATE, B_CNT
FROM
(
	SELECT ROWNUM N, B_NO, S_NO, B_TITLE, B_WRITER, B_CONTENT, B_REGDATE, B_CNT
	FROM DATA_BOARD
	WHERE S_NO = #{studyno}
	ORDER BY B_NO DESC
)
WHERE N >= #{startRow} AND N <= #{endRow}

mapper.update:
